import { useRouter } from 'next/router';
import { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import { FiCopy, FiSun, FiMoon, FiSave, FiEdit, FiHeart } from 'react-icons/fi';
import toast, { Toaster } from 'react-hot-toast';

export default function Share() {
  const router = useRouter();
  const { id } = router.query;
  const [text, setText] = useState('');
  const [originalText, setOriginalText] = useState('');
  const [isEditing, setIsEditing] = useState(false);
  const [isEditable, setIsEditable] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [likesCount, setLikesCount] = useState(0);
  const [hasLiked, setHasLiked] = useState(false);
  const [isDarkMode, setIsDarkMode] = useState(() => {
    if (typeof window !== 'undefined') {
      return localStorage.getItem('theme') === 'dark' || true;
    }
    return true;
  });

  // Persist theme
  useEffect(() => {
    localStorage.setItem('theme', isDarkMode ? 'dark' : 'light');
    document.documentElement.classList.toggle('dark', isDarkMode);
  }, [isDarkMode]);

  useEffect(() => {
    if (id) {
      fetch('/api/clipboard')
        .then((res) => res.json())
        .then((data) => {
          const item = data.find((item) => item.id === id);
          if (item) {
            setText(item.text);
            setOriginalText(item.text);
            setIsEditable(!!item.editable);
            setLikesCount(item.likesCount || 0);
            setHasLiked(item.hasLiked || false);
          } else {
            setText('Text not found');
            setIsEditable(false);
          }
        })
        .catch(() => {
          setText('Error loading text');
          setIsEditable(false);
          toast.error('Failed to load shared text');
        });
    }
  }, [id]);

  const handleCopy = () => {
    navigator.clipboard.writeText(text);
    toast.success('Copied to clipboard!', { icon: 'ðŸ“‹' });
  };

  const toggleTheme = () => {
    setIsDarkMode((prev) => !prev);
  };

  const handleEdit = () => {
    if (!isEditable) {
      toast.error('This text cannot be edited');
      return;
    }
    setIsEditing(true);
  };

  const handleSave = async () => {
    if (!isEditable) {
      toast.error('This text cannot be edited');
      return;
    }

    if (!text.trim()) {
      toast.error('Text cannot be empty');
      return;
    }

    try {
      setIsLoading(true);
      const res = await fetch('/api/clipboard', {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ id, text }),
      });

      if (!res.ok) {
        const error = await res.json();
        throw new Error(error.error || 'Failed to save');
      }

      const savedItem = await res.json();
      setText(savedItem.text);
      setOriginalText(savedItem.text);
      setIsEditing(false);
      toast.success('Changes saved!', { icon: 'âœ…' });
    } catch (error) {
      toast.error(error.message);
      if (error.message === 'This item cannot be edited') {
        setIsEditable(false);
      }
    } finally {
      setIsLoading(false);
    }
  };

  const handleCancel = () => {
    setText(originalText);
    setIsEditing(false);
  };

  const handleLike = async () => {
    try {
      const res = await fetch('/api/clipboard', {
        method: 'PATCH',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ id, action: 'like' }),
      });

      if (!res.ok) throw new Error('Failed to update like');

      const data = await res.json();
      setLikesCount(data.likesCount);
      setHasLiked(data.hasLiked);

      toast.success(data.hasLiked ? 'Added to favorites! ðŸ’–' : 'Removed from favorites');
    } catch (error) {
      toast.error('Failed to update like');
    }
  };

  return (
    <div
      className={`min-h-screen ${
        isDarkMode ? 'bg-gray-900 text-white' : 'bg-gray-100 text-gray-900'
      } transition-colors duration-500`}
    >
      <Toaster position="top-right" />

      {/* Header */}
      <motion.header
        initial={{ opacity: 0, y: -50 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5 }}
        className="sticky top-0 z-50 w-full px-4 py-3 backdrop-blur-lg bg-opacity-90 border-b flex items-center justify-between"
      >
        <h1 className="text-xl sm:text-2xl md:text-3xl font-bold bg-clip-text text-transparent bg-gradient-to-r from-blue-500 to-purple-500">
          Clipboard Pro
        </h1>
        <motion.button
          whileHover={{ rotate: 180 }}
          onClick={toggleTheme}
          className={`p-2 rounded-full ${
            isDarkMode ? 'bg-gray-700' : 'bg-gray-200'
          } hover:bg-opacity-80`}
          aria-label="Toggle theme"
        >
          {isDarkMode ? <FiSun size={20} /> : <FiMoon size={20} />}
        </motion.button>
      </motion.header>

      {/* Main Content */}
      <main className="p-4 lg:p-6 max-w-3xl mx-auto w-full">
        <motion.div
          initial={{ opacity: 0, scale: 0.95 }}
          animate={{ opacity: 1, scale: 1 }}
          transition={{ duration: 0.5 }}
          className={`w-full rounded-xl overflow-hidden ${
            isDarkMode
              ? 'bg-gray-800/80 backdrop-blur-md shadow-lg'
              : 'bg-white/90 backdrop-blur-md shadow-md'
          }`}
        >
          <div className="p-6">
            <div className="flex justify-between items-center mb-4">
              <div className="flex items-center gap-4">
                <h2 className="text-xl font-semibold">Shared Clipboard</h2>
                <motion.button
                  whileHover={{ scale: 1.1 }}
                  whileTap={{ scale: 0.9 }}
                  onClick={handleLike}
                  className={`p-2 rounded-full flex items-center gap-1 ${
                    hasLiked
                      ? 'bg-pink-500 text-white'
                      : isDarkMode
                      ? 'bg-gray-700 hover:bg-gray-600'
                      : 'bg-gray-200 hover:bg-gray-300'
                  }`}
                >
                  <FiHeart
                    size={20}
                    className={hasLiked ? 'fill-current' : ''}
                  />
                  <span className="text-sm font-medium">{likesCount}</span>
                </motion.button>
              </div>
              <div className="flex items-center gap-2">
                {!isEditing && (
                  <motion.button
                    whileHover={{ scale: 1.02 }}
                    whileTap={{ scale: 0.98 }}
                    onClick={handleCopy}
                    disabled={text === 'Text not found' || text === 'Error loading text'}
                    className={`px-4 py-2 rounded-lg text-sm font-medium transition-all flex items-center gap-2 ${
                      isDarkMode
                        ? 'bg-gray-700 hover:bg-gray-600'
                        : 'bg-gray-200 hover:bg-gray-300'
                    } disabled:opacity-50 disabled:cursor-not-allowed`}
                  >
                    <FiCopy size={16} />
                    <span>Copy</span>
                  </motion.button>
                )}
                {isEditable && !isEditing && (
                  <motion.button
                    whileHover={{ scale: 1.05 }}
                    whileTap={{ scale: 0.95 }}
                    onClick={handleEdit}
                    className={`flex items-center gap-2 px-4 py-2 rounded-lg text-sm font-medium ${
                      isDarkMode 
                        ? 'bg-blue-600 hover:bg-blue-500 text-white' 
                        : 'bg-blue-500 hover:bg-blue-400 text-white'
                    }`}
                  >
                    <FiEdit size={16} />
                    <span>Edit</span>
                  </motion.button>
                )}
              </div>
            </div>
            <div className={`relative group ${isEditing ? 'mb-4' : ''}`}>
              {isEditing ? (
                <div className="relative">
                  <textarea
                    value={text}
                    onChange={(e) => setText(e.target.value)}
                    className={`w-full p-4 rounded-lg font-mono text-sm min-h-[300px] resize-y ${
                      isDarkMode 
                        ? 'bg-gray-700/50 focus:bg-gray-700/70' 
                        : 'bg-gray-50 focus:bg-gray-100'
                    } focus:outline-none focus:ring-2 focus:ring-blue-500 transition-colors`}
                    placeholder="Enter text..."
                    style={{
                      boxShadow: isDarkMode 
                        ? '0 0 0 1px rgba(59, 130, 246, 0.1)' 
                        : '0 0 0 1px rgba(59, 130, 246, 0.05)'
                    }}
                  />
                  <div className="absolute bottom-4 right-4 flex items-center gap-2 opacity-0 group-hover:opacity-100 transition-opacity">
                    <span className={`text-xs ${isDarkMode ? 'text-gray-400' : 'text-gray-500'}`}>
                      Press Esc to cancel
                    </span>
                    <span className={`text-xs ${isDarkMode ? 'text-gray-400' : 'text-gray-500'}`}>
                      â€¢
                    </span>
                    <span className={`text-xs ${isDarkMode ? 'text-gray-400' : 'text-gray-500'}`}>
                      Ctrl + Enter to save
                    </span>
                  </div>
                </div>
              ) : (
                <pre
                  className={`p-4 rounded-lg font-mono text-sm break-all whitespace-pre-wrap relative group ${
                    isDarkMode ? 'bg-gray-700/50' : 'bg-gray-50'
                  } transition-colors min-h-[300px]`}
                  style={{
                    boxShadow: isDarkMode 
                      ? '0 0 0 1px rgba(59, 130, 246, 0.1)' 
                      : '0 0 0 1px rgba(59, 130, 246, 0.05)'
                  }}
                >
                  {text}
                </pre>
              )}
            </div>
            {isEditing && (
              <div className="flex flex-wrap gap-2">
                <motion.button
                  whileHover={{ scale: 1.02 }}
                  whileTap={{ scale: 0.98 }}
                  onClick={handleCancel}
                  className={`px-4 py-2 rounded-lg text-sm font-medium ${
                    isDarkMode
                      ? 'bg-gray-700 hover:bg-gray-600'
                      : 'bg-gray-200 hover:bg-gray-300'
                  }`}
                >
                  Cancel
                </motion.button>
                <motion.button
                  whileHover={{ scale: 1.02 }}
                  whileTap={{ scale: 0.98 }}
                  onClick={handleSave}
                  disabled={isLoading}
                  className={`px-6 py-2 rounded-lg text-sm font-medium transition-all flex items-center justify-center gap-2 ${
                    isDarkMode
                      ? 'bg-blue-600 hover:bg-blue-500 text-white'
                      : 'bg-blue-500 hover:bg-blue-400 text-white'
                  } ${isLoading ? 'opacity-50 cursor-not-allowed' : ''}`}
                >
                  {isLoading ? (
                    <div className="w-4 h-4 border-2 border-white/30 border-t-white rounded-full animate-spin" />
                  ) : (
                    <FiSave size={16} />
                  )}
                  Save Changes
                </motion.button>
              </div>
            )}
          </div>
        </motion.div>
      </main>
    </div>
  );
}
